        } else if (selectedExamBody === 'TQUK|0') {
            $examTypeField.append('<option class="paper" value="Paper-Based (London)">Paper-Based Exam (London)</option>');
            $examTypeField.append('<option value="Paper-Based (Birmingham)">Paper-Based Exam (Birmingham)</option>');
            $examTypeField.append('<option value="Paper-Based (Swindon)">Paper-Based Exam (Swindon)</option>');
            $examTypeField.append('<option value="Test">Remote Exam from Home</option>');
        }
        $examTypeField.find('option:first').prop('selected', true);
        $examTypeField.trigger('change');
    });
    jQuery('#input_89_89').change(function(){
        let selectedExamBody = jQuery('#input_89_7').val();
        let examTypeField = jQuery('#input_89_43');
        if (selectedExamBody === 'NCFE|0') {
            if (jQuery(this).val() === 'Digital Functional Skills') {
                examTypeField.val('NCFE Remotely').trigger('change');
                examTypeField.find('option').filter(function(){ 
